contracts/CertificateNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

/**
 * @title CertificateNFT (Optimized)
 * @dev High-efficiency NFT contract for Vignan Institute.
 * Uses event logging for data persistence instead of expensive storage.
 */
contract CertificateNFT is ERC721URIStorage, AccessControl, Pausable {
    using Strings for uint256;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");

    enum CertificateType { REGULAR, SEMESTER, ACHIEVEMENT, CUSTOM }

    // Optimization constants
    uint256 public constant MAX_BATCH_SIZE = 50;

    // Custom Errors
    error InvalidInput(); 
    error Unauthorized();
    error NotFound();
    error AlreadyExists();
    error Revoked();
    error BatchLimit();
    error LengthMismatch();

    // Minimal State
    uint256 private _tokenIdCounter;
    string private _baseTokenURI;
    
    // Mapping from token ID to minimal metadata
    mapping(uint256 => CertificateConfig) public certConfigs;
    // Mapping from student to list of their token IDs
    mapping(address => uint256[]) public studentCertificates;
    // Prevent duplicates
    mapping(string => bool) public usedHashes;
    mapping(string => bool) public usedUniqueIdentifiers; // SerialNos, MemoNos merged map

    struct CertificateConfig {
        CertificateType certType;
        bool isRevoked;
        address issuer;
        uint256 issueDate;
    }

    // Events - These serve as the permanent record of data
    event CertificateIssued(
        uint256 indexed tokenId,
        address indexed student,
        string ipfsHash,
        CertificateType certType,
        uint256 timestamp
    );
    
    // Detailed log events for indexing (gas cheaper than storage)
    event RegularCertDetails(uint256 indexed id, string course, string grade);
    event SemesterCertDetails(uint256 indexed id, string serialNo, string memoNo, uint256 sgpa);
    event AchievementCertDetails(uint256 indexed id, string title, string category);
    event CustomCertDetails(uint256 indexed id, string templateId);
    
    event CertRevoked(uint256 indexed tokenId, address admin);
    event CertVerified(uint256 indexed tokenId, address verifier, bool isValid);

    struct SemesterParams {
        string serialNo;
        string memoNo;
        string regdNo;
        string branch;
        string examination;
        uint256 sgpa; // Pre-calculated
    }

    constructor() ERC721("Vignan Certificate", "VIGNAN") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
    }

    // --- Core Internal Logic ---

    function _mintBase(address to, string calldata ipfsHash, CertificateType cType) internal returns (uint256) {
        if (to == address(0)) revert InvalidInput();
        if (bytes(ipfsHash).length == 0) revert InvalidInput();
        if (usedHashes[ipfsHash]) revert AlreadyExists();

        _tokenIdCounter++;
        uint256 tokenId = _tokenIdCounter;

        _safeMint(to, tokenId);
        _setTokenURI(tokenId, ipfsHash); // In URIStorage, this sets the full string usually. 
        // If we want to use baseURI + ID, we would just store the hash. 
        // For compatibility with previous logic:
        // We will store just the ipfsHash in the map if needed, but URIStorage stores string.
        
        certConfigs[tokenId] = CertificateConfig({
            certType: cType,
            isRevoked: false,
            issuer: msg.sender,
            issueDate: block.timestamp
        });

        studentCertificates[to].push(tokenId);
        usedHashes[ipfsHash] = true;

        emit CertificateIssued(tokenId, to, ipfsHash, cType, block.timestamp);
        return tokenId;
    }

    // --- Public Minting Functions ---

    /**
     * @dev Batch mint regular certificates. 
     * Note: Arrays must be equal length.
     */
    function mintCertificatesBatch(
        address[] calldata students,
        string[] calldata courseNames,
        string[] calldata grades,
        string[] calldata ipfsHashes
    ) external onlyRole(MINTER_ROLE) whenNotPaused {
        uint256 len = students.length;
        if (len > MAX_BATCH_SIZE) revert BatchLimit();
        if (courseNames.length != len || grades.length != len || ipfsHashes.length != len) revert LengthMismatch();

        for (uint256 i = 0; i < len; i++) {
            uint256 tid = _mintBase(students[i], ipfsHashes[i], CertificateType.REGULAR);
            emit RegularCertDetails(tid, courseNames[i], grades[i]);
        }
    }

    /**
     * @dev Mint single regular certificate (Legacy support wrapper)
     */
    function mintCertificate(
        address student,
        string calldata courseName,
        string calldata grade,
        string calldata ipfsHash
    ) external onlyRole(MINTER_ROLE) whenNotPaused returns (uint256) {
        uint256 tid = _mintBase(student, ipfsHash, CertificateType.REGULAR);
        emit RegularCertDetails(tid, courseName, grade);
        return tid;
    }

    /**
     * @dev Mint semester certificate.
     * simplified to avoid stack too deep.
     */
    function mintSemesterCertificate(
        address student,
        SemesterParams calldata params,
        string calldata ipfsHash
    ) external onlyRole(MINTER_ROLE) whenNotPaused returns (uint256) {
        if (usedUniqueIdentifiers[params.serialNo] || usedUniqueIdentifiers[params.memoNo]) revert AlreadyExists();
        
        uint256 tid = _mintBase(student, ipfsHash, CertificateType.SEMESTER);
        
        usedUniqueIdentifiers[params.serialNo] = true;
        usedUniqueIdentifiers[params.memoNo] = true;

        emit SemesterCertDetails(tid, params.serialNo, params.memoNo, params.sgpa);
        return tid;
    }

    /**
     * @dev Mint achievement certificate
     */
    function mintAchievementCertificate(
        address student,
        string calldata title,
        string calldata category,
        string calldata ipfsHash,
        address[] calldata /* verifiers */ // kept for API compat, but unused on-chain
    ) external onlyRole(MINTER_ROLE) whenNotPaused returns (uint256) {
        uint256 tid = _mintBase(student, ipfsHash, CertificateType.ACHIEVEMENT);
        emit AchievementCertDetails(tid, title, category);
        return tid;
    }

    /**
     * @dev Mint custom certificate
     */
    function mintCustomCertificate(
        address student,
        string calldata templateId,
        string[] calldata /* fieldNames */, // Data should be in IPFS
        string[] calldata /* fieldValues */,
        string calldata ipfsHash
    ) external onlyRole(MINTER_ROLE) whenNotPaused returns (uint256) {
        uint256 tid = _mintBase(student, ipfsHash, CertificateType.CUSTOM);
        emit CustomCertDetails(tid, templateId);
        return tid;
    }

    // --- Admin & Verification ---

    function revokeCertificate(uint256 tokenId) external onlyRole(ADMIN_ROLE) {
        if (_ownerOf(tokenId) == address(0)) revert NotFound();
        if (certConfigs[tokenId].isRevoked) revert Revoked();
        
        certConfigs[tokenId].isRevoked = true;
        emit CertRevoked(tokenId, msg.sender);
    }

    function verifyCertificate(uint256 tokenId) external view returns (bool isValid, string memory ipfsUri, CertificateType cType) {
        if (_ownerOf(tokenId) == address(0)) return (false, "", CertificateType.REGULAR);
        
        CertificateConfig memory conf = certConfigs[tokenId];
        if (conf.isRevoked) return (false, "", conf.certType);

        return (true, tokenURI(tokenId), conf.certType);
    }
    
    // Verifier role action
    function verifyCertificateByVerifier(uint256 tokenId) external onlyRole(VERIFIER_ROLE) returns (bool, CertificateType) {
         (bool valid, , CertificateType cType) = this.verifyCertificate(tokenId);
         emit CertVerified(tokenId, msg.sender, valid);
         return (valid, cType);
    }

    function getStudentCertificates(address student) external view returns (uint256[] memory) {
        return studentCertificates[student];
    }

    // Minimal Utils
    function setBaseURI(string memory baseURI) external onlyRole(ADMIN_ROLE) {
        _baseTokenURI = baseURI;
    }
    
    function pause() external onlyRole(ADMIN_ROLE) { _pause(); }
    function unpause() external onlyRole(ADMIN_ROLE) { _unpause(); }
    
    // Overrides
    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        return super.tokenURI(tokenId);
    }
    
    function supportsInterface(bytes4 interfaceId) public view override(ERC721URIStorage, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

contracts/ScholarshipEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./CertificateNFT.sol";

/**
 * @title ScholarshipEscrow
 * @dev Smart contract for managing scholarship funds and disbursements
 * @author Vignan Institute
 */
contract ScholarshipEscrow is AccessControl, Pausable, ReentrancyGuard {
    // Roles
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant SCHOLARSHIP_MANAGER_ROLE = keccak256("SCHOLARSHIP_MANAGER_ROLE");
    
    // State variables
    uint256 private _scholarshipIdCounter;
    mapping(uint256 => Scholarship) public scholarships;
    mapping(address => uint256[]) public studentScholarships;
    mapping(address => uint256) public totalClaimed;
    mapping(uint256 => mapping(address => bool)) public scholarshipClaims; // scholarshipId => student => claimed
    mapping(uint256 => EligibilityCriteria) public scholarshipCriteria;
    
    // Certificate NFT contract
    CertificateNFT public certificateNFT;
    
    // Events
    event ScholarshipCreated(
        uint256 indexed scholarshipId,
        string name,
        uint256 totalAmount,
        uint256 maxRecipients,
        uint256 deadline,
        uint256 timestamp
    );
    
    event ScholarshipClaimed(
        uint256 indexed scholarshipId,
        address indexed student,
        uint256 amount,
        uint256 timestamp
    );
    
    event ScholarshipRevoked(uint256 indexed scholarshipId, address indexed admin, uint256 timestamp);
    
    event FundsDeposited(address indexed depositor, uint256 amount, uint256 timestamp);
    event FundsWithdrawn(address indexed admin, uint256 amount, uint256 timestamp);
    
    // Structs
    struct Scholarship {
        string name;
        string description;
        uint256 totalAmount;
        uint256 claimedAmount;
        uint256 remainingAmount;
        uint256 maxRecipients;
        uint256 amountPerRecipient;
        uint256 createdAt;
        uint256 deadline;
        bool isActive;
        address createdBy;
        address tokenAddress; // 0x0 for ETH, contract address for ERC20
        string tokenSymbol;
    }

    struct EligibilityCriteria {
        uint256 minGPA; // GPA * 100 (e.g., 350 for 3.5 GPA)
        string[] requiredCourses;
        string[] allowedDepartments;
        uint256 minCertificates;
        uint256 enrollmentAfter; // timestamp
        uint256 enrollmentBefore; // timestamp
        bool requiresAllCourses; // true = AND, false = OR
    }
    
    constructor(address _certificateNFT) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(SCHOLARSHIP_MANAGER_ROLE, msg.sender);
        
        certificateNFT = CertificateNFT(_certificateNFT);
    }
    
    /**
     * @dev Create a new scholarship with eligibility criteria
     */
    function createScholarship(
        string memory name,
        string memory description,
        uint256 totalAmount,
        uint256 maxRecipients,
        uint256 deadline,
        address tokenAddress,
        string memory tokenSymbol,
        EligibilityCriteria memory criteria
    ) external payable onlyRole(SCHOLARSHIP_MANAGER_ROLE) whenNotPaused returns (uint256) {
        require(totalAmount > 0, "Amount must be greater than 0");
        require(maxRecipients > 0, "Max recipients must be greater than 0");
        require(deadline > block.timestamp, "Deadline must be in the future");
        require(bytes(name).length > 0, "Name cannot be empty");
        
        // For ETH scholarships, check msg.value
        if (tokenAddress == address(0)) {
            require(msg.value >= totalAmount, "Insufficient ETH sent");
        }
        
        _scholarshipIdCounter++;
        uint256 scholarshipId = _scholarshipIdCounter;
        
        uint256 amountPerRecipient = totalAmount / maxRecipients;
        
        scholarships[scholarshipId] = Scholarship({
            name: name,
            description: description,
            totalAmount: totalAmount,
            claimedAmount: 0,
            remainingAmount: totalAmount,
            maxRecipients: maxRecipients,
            amountPerRecipient: amountPerRecipient,
            createdAt: block.timestamp,
            deadline: deadline,
            isActive: true,
            createdBy: msg.sender,
            tokenAddress: tokenAddress,
            tokenSymbol: tokenSymbol
        });
        
        scholarshipCriteria[scholarshipId] = criteria;
        
        emit ScholarshipCreated(scholarshipId, name, totalAmount, maxRecipients, deadline, block.timestamp);
        
        return scholarshipId;
    }
    
    /**
     * @dev Claim scholarship funds (students only)
     */
    function claimScholarship(uint256 scholarshipId) external whenNotPaused nonReentrant {
        Scholarship storage scholarship = scholarships[scholarshipId];
        
        require(scholarship.isActive, "Scholarship is not active");
        require(block.timestamp <= scholarship.deadline, "Scholarship deadline has passed");
        require(!scholarshipClaims[scholarshipId][msg.sender], "Already claimed this scholarship");
        require(scholarship.remainingAmount > 0, "No funds remaining");
        require(scholarship.claimedAmount < scholarship.totalAmount, "Scholarship fully claimed");
        
        // Check eligibility
        require(isEligibleForScholarship(scholarshipId, msg.sender), "Student not eligible for this scholarship");
        
        uint256 claimAmount = scholarship.amountPerRecipient;
        require(claimAmount <= scholarship.remainingAmount, "Insufficient funds for claim");
        
        // Mark as claimed
        scholarshipClaims[scholarshipId][msg.sender] = true;
        
        // Update scholarship data
        scholarship.claimedAmount += claimAmount;
        scholarship.remainingAmount -= claimAmount;
        
        // Mark as inactive if fully claimed
        if (scholarship.remainingAmount == 0 || scholarship.claimedAmount >= scholarship.totalAmount) {
            scholarship.isActive = false;
        }
        
        // Transfer funds
        if (scholarship.tokenAddress == address(0)) {
            // ETH transfer
            require(address(this).balance >= claimAmount, "Insufficient contract balance");
            (bool success, ) = payable(msg.sender).call{value: claimAmount}("");
            require(success, "ETH transfer failed");
        } else {
            // ERC20 transfer (to be implemented with IERC20 interface)
            revert("ERC20 transfers not yet implemented");
        }
        
        totalClaimed[msg.sender] += claimAmount;
        studentScholarships[msg.sender].push(scholarshipId);
        
        emit ScholarshipClaimed(scholarshipId, msg.sender, claimAmount, block.timestamp);
    }
    
    /**
     * @dev Check if student is eligible for a specific scholarship (Simplified)
     */
    function isEligibleForScholarship(uint256 scholarshipId, address student) public view returns (bool) {
        EligibilityCriteria memory criteria = scholarshipCriteria[scholarshipId];
        
        // Get student's certificates
        uint256[] memory studentCerts = certificateNFT.getStudentCertificates(student);
        
        // Check minimum certificates requirement
        if (studentCerts.length < criteria.minCertificates) {
            return false;
        }
        
        // Note: Advanced course/department checking removed as data is now Off-Chain/Encoded.
        // For production, this logic needs to verify via Oracle or signed claims, 
        // or check simple metadata (CertificateType) which IS available.
        // For now, we assume simple eligibility based on holding certificates.
        
        return true;
    }
    
    /**
     * @dev Check if student has required courses (Verified off-chain for now)
     */
    function hasRequiredCertificates(address student) public view returns (bool) {
         uint256[] memory studentCerts = certificateNFT.getStudentCertificates(student);
         return studentCerts.length > 0;
    }

    /**
     * @dev Revoke a scholarship
     */
    function revokeScholarship(uint256 scholarshipId) external onlyRole(ADMIN_ROLE) {
        Scholarship storage scholarship = scholarships[scholarshipId];
        
        require(scholarship.isActive, "Scholarship is not active");
        
        scholarship.isActive = false;
        
        emit ScholarshipRevoked(scholarshipId, msg.sender, block.timestamp);
    }
    
    /**
     * @dev Deposit funds to the escrow
     */
    function depositFunds() external payable whenNotPaused {
        require(msg.value > 0, "Amount must be greater than 0");
        emit FundsDeposited(msg.sender, msg.value, block.timestamp);
    }
    
    /**
     * @dev Withdraw funds from the escrow (admin only)
     */
    function withdrawFunds(uint256 amount) external onlyRole(ADMIN_ROLE) nonReentrant {
        require(amount <= address(this).balance, "Insufficient balance");
        require(amount > 0, "Amount must be greater than 0");
        
        (bool success, ) = payable(msg.sender).call{value: amount}("");
        require(success, "Transfer failed");
        
        emit FundsWithdrawn(msg.sender, amount, block.timestamp);
    }
    
    /**
     * @dev Get scholarship details
     */
    function getScholarship(uint256 scholarshipId) external view returns (Scholarship memory) {
        return scholarships[scholarshipId];
    }
    
    /**
     * @dev Get all scholarships for a student
     */
    function getStudentScholarships(address student) external view returns (uint256[] memory) {
        return studentScholarships[student];
    }
    
    /**
     * @dev Get total claimed amount for a student
     */
    function getTotalClaimed(address student) external view returns (uint256) {
        return totalClaimed[student];
    }
    
    /**
     * @dev Get contract balance
     */
    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }
    
    /**
     * @dev Pause contract
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }
    
    /**
     * @dev Unpause contract
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }
    
    /**
     * @dev Receive function to accept ETH
     */
    receive() external payable {
        emit FundsDeposited(msg.sender, msg.value, block.timestamp);
    }
}

contracts/VignanRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title VignanRegistry
 * @dev Central registry for managing students, courses, and institutional data
 * @author Vignan Institute
 */
contract VignanRegistry is AccessControl, Pausable {
    // Roles
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant REGISTRAR_ROLE = keccak256("REGISTRAR_ROLE");
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");
    
    // State variables
    uint256 private _studentIdCounter;
    uint256 private _courseIdCounter;
    
    mapping(uint256 => Student) public students;
    mapping(address => uint256) public addressToStudentId;
    mapping(uint256 => Course) public courses;
    mapping(string => bool) public usedStudentNumbers;
    mapping(address => bool) public registeredStudents;
    
    // Events
    event StudentRegistered(
        uint256 indexed studentId,
        address indexed studentAddress,
        string studentNumber,
        string name,
        uint256 timestamp
    );
    
    event StudentUpdated(
        uint256 indexed studentId,
        address indexed studentAddress,
        string name,
        uint256 timestamp
    );
    
    event CourseCreated(
        uint256 indexed courseId,
        string courseCode,
        string courseName,
        uint256 credits,
        uint256 timestamp
    );
    
    event CourseUpdated(
        uint256 indexed courseId,
        string courseName,
        uint256 credits,
        uint256 timestamp
    );
    
    event StudentEnrolled(
        uint256 indexed studentId,
        uint256 indexed courseId,
        uint256 timestamp
    );
    
    event StudentGraduated(
        uint256 indexed studentId,
        string degree,
        uint256 timestamp
    );
    
    // Structs
    struct Student {
        uint256 studentId;
        address studentAddress;
        string studentNumber;
        string name;
        string email;
        string department;
        string program;
        uint256 enrollmentDate;
        uint256 graduationDate;
        bool isActive;
        bool isGraduated;
        string degree;
        uint256[] enrolledCourses;
    }
    
    struct Course {
        uint256 courseId;
        string courseCode;
        string courseName;
        string description;
        uint256 credits;
        string department;
        bool isActive;
        uint256 createdAt;
        address createdBy;
    }
    
    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(REGISTRAR_ROLE, msg.sender);
        _grantRole(VERIFIER_ROLE, msg.sender);
    }
    
    /**
     * @dev Register a new student
     * @param studentAddress Address of the student
     * @param studentNumber Unique student number
     * @param name Name of the student
     * @param email Email of the student
     * @param department Department of the student
     * @param program Program of the student
     */
    function registerStudent(
        address studentAddress,
        string memory studentNumber,
        string memory name,
        string memory email,
        string memory department,
        string memory program
    ) external onlyRole(REGISTRAR_ROLE) whenNotPaused returns (uint256) {
        require(studentAddress != address(0), "Invalid student address");
        require(bytes(studentNumber).length > 0, "Student number cannot be empty");
        require(bytes(name).length > 0, "Name cannot be empty");
        require(!usedStudentNumbers[studentNumber], "Student number already exists");
        require(!registeredStudents[studentAddress], "Address already registered");
        
        _studentIdCounter++;
        uint256 studentId = _studentIdCounter;
        
        students[studentId] = Student({
            studentId: studentId,
            studentAddress: studentAddress,
            studentNumber: studentNumber,
            name: name,
            email: email,
            department: department,
            program: program,
            enrollmentDate: block.timestamp,
            graduationDate: 0,
            isActive: true,
            isGraduated: false,
            degree: "",
            enrolledCourses: new uint256[](0)
        });
        
        addressToStudentId[studentAddress] = studentId;
        usedStudentNumbers[studentNumber] = true;
        registeredStudents[studentAddress] = true;
        
        emit StudentRegistered(studentId, studentAddress, studentNumber, name, block.timestamp);
        
        return studentId;
    }
    
    /**
     * @dev Update student information
     * @param studentId ID of the student
     * @param name New name
     * @param email New email
     * @param department New department
     * @param program New program
     */
    function updateStudent(
        uint256 studentId,
        string memory name,
        string memory email,
        string memory department,
        string memory program
    ) external onlyRole(REGISTRAR_ROLE) {
        require(students[studentId].studentId != 0, "Student does not exist");
        require(students[studentId].isActive, "Student is not active");
        
        students[studentId].name = name;
        students[studentId].email = email;
        students[studentId].department = department;
        students[studentId].program = program;
        
        emit StudentUpdated(studentId, students[studentId].studentAddress, name, block.timestamp);
    }
    
    /**
     * @dev Create a new course
     * @param courseCode Unique course code
     * @param courseName Name of the course
     * @param description Description of the course
     * @param credits Number of credits
     * @param department Department offering the course
     */
    function createCourse(
        string memory courseCode,
        string memory courseName,
        string memory description,
        uint256 credits,
        string memory department
    ) external onlyRole(REGISTRAR_ROLE) whenNotPaused returns (uint256) {
        require(bytes(courseCode).length > 0, "Course code cannot be empty");
        require(bytes(courseName).length > 0, "Course name cannot be empty");
        require(credits > 0, "Credits must be greater than 0");
        
        _courseIdCounter++;
        uint256 courseId = _courseIdCounter;
        
        courses[courseId] = Course({
            courseId: courseId,
            courseCode: courseCode,
            courseName: courseName,
            description: description,
            credits: credits,
            department: department,
            isActive: true,
            createdAt: block.timestamp,
            createdBy: msg.sender
        });
        
        emit CourseCreated(courseId, courseCode, courseName, credits, block.timestamp);
        
        return courseId;
    }
    
    /**
     * @dev Update course information
     * @param courseId ID of the course
     * @param courseName New course name
     * @param description New description
     * @param credits New credits
     */
    function updateCourse(
        uint256 courseId,
        string memory courseName,
        string memory description,
        uint256 credits
    ) external onlyRole(REGISTRAR_ROLE) {
        require(courses[courseId].courseId != 0, "Course does not exist");
        require(courses[courseId].isActive, "Course is not active");
        
        courses[courseId].courseName = courseName;
        courses[courseId].description = description;
        courses[courseId].credits = credits;
        
        emit CourseUpdated(courseId, courseName, credits, block.timestamp);
    }
    
    /**
     * @dev Enroll a student in a course
     * @param studentId ID of the student
     * @param courseId ID of the course
     */
    function enrollStudent(uint256 studentId, uint256 courseId) external onlyRole(REGISTRAR_ROLE) {
        require(students[studentId].studentId != 0, "Student does not exist");
        require(students[studentId].isActive, "Student is not active");
        require(courses[courseId].courseId != 0, "Course does not exist");
        require(courses[courseId].isActive, "Course is not active");
        
        students[studentId].enrolledCourses.push(courseId);
        
        emit StudentEnrolled(studentId, courseId, block.timestamp);
    }
    
    /**
     * @dev Mark a student as graduated
     * @param studentId ID of the student
     * @param degree Degree awarded
     */
    function graduateStudent(uint256 studentId, string memory degree) external onlyRole(REGISTRAR_ROLE) {
        require(students[studentId].studentId != 0, "Student does not exist");
        require(students[studentId].isActive, "Student is not active");
        require(!students[studentId].isGraduated, "Student already graduated");
        
        students[studentId].isGraduated = true;
        students[studentId].graduationDate = block.timestamp;
        students[studentId].degree = degree;
        
        emit StudentGraduated(studentId, degree, block.timestamp);
    }
    
    /**
     * @dev Deactivate a student
     * @param studentId ID of the student
     */
    function deactivateStudent(uint256 studentId) external onlyRole(ADMIN_ROLE) {
        require(students[studentId].studentId != 0, "Student does not exist");
        students[studentId].isActive = false;
    }
    
    /**
     * @dev Deactivate a course
     * @param courseId ID of the course
     */
    function deactivateCourse(uint256 courseId) external onlyRole(ADMIN_ROLE) {
        require(courses[courseId].courseId != 0, "Course does not exist");
        courses[courseId].isActive = false;
    }
    
    /**
     * @dev Get student information
     * @param studentId ID of the student
     * @return Student struct
     */
    function getStudent(uint256 studentId) external view returns (Student memory) {
        return students[studentId];
    }
    
    /**
     * @dev Get student by address
     * @param studentAddress Address of the student
     * @return Student struct
     */
    function getStudentByAddress(address studentAddress) external view returns (Student memory) {
        uint256 studentId = addressToStudentId[studentAddress];
        require(studentId != 0, "Student not found");
        return students[studentId];
    }
    
    /**
     * @dev Get course information
     * @param courseId ID of the course
     * @return Course struct
     */
    function getCourse(uint256 courseId) external view returns (Course memory) {
        return courses[courseId];
    }
    
    /**
     * @dev Check if address is registered
     * @param studentAddress Address to check
     * @return True if registered
     */
    function isStudentRegistered(address studentAddress) external view returns (bool) {
        return registeredStudents[studentAddress];
    }
    
    /**
     * @dev Get total number of students
     * @return Total student count
     */
    function getTotalStudents() external view returns (uint256) {
        return _studentIdCounter;
    }
    
    /**
     * @dev Get total number of courses
     * @return Total course count
     */
    function getTotalCourses() external view returns (uint256) {
        return _courseIdCounter;
    }
    
    /**
     * @dev Pause contract
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }
    
    /**
     * @dev Unpause contract
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }
}

